let compress =
  (a,q)=>eval(a.map((_,i)=>i?"for(m=a.shift();m>a[0];m--,q--)n+="+[1,...Array(i)].map((w,e)=>w||`(q+${e-1})/`+e).join`*`:"a.sort((a,b)=>b-a),n=a.shift()-a[0]").join`;`)

let decompress =
  (n,q,i)=>eval("a=[],m=0;"+[...Array(i)].map(_=>`for(;s=${[1,...Array(--i)].map((w,e)=>w||`(q+${e-1})/`+e).join`*`},n>=s;m++,q--)n-=s;a=[...a,m]`).join`;`)
